mapper.select:
SELECT * FROM (SELECT rownum as rn, a.* FROM (
SELECT board_no, title, content, writer, writer_date, click_cnt, image FROM board
ORDER BY board_no desc ) a WHERE rownum <= #{end} ) b WHERE rn >= #{start}

mapper.insert:

	select board_seq.nextval from dual

INSERT INTO board
			(board_no,
			title,
