			image)
    VALUES  (
    		#{boardNo}
    		,#{title}
    		,#{content}
    		,#{writer}
    		,#{writerDate}
    		,#{clickCnt}
    		,#{image}
    		)

mapper.update:
UPDATE board
SET title = #{title},
	content = #{content}
WHERE board_no = #{boardNo}


	select click_cnt + 1
	from board
	where board_no = #{boardNo}

UPDATE board
SET click_cnt = #{clickCnt}
WHERE board_no = #{boardNo}
